import React, {Suspense} from 'react'
import './Random.scss'
import {getRandom} from '../redux/actions'
import { useDispatch, useSelector } from 'react-redux';
import Button from '@material-ui/core/Button';
import {useImage} from 'react-image'

function MyImageComponent() {
    const Img = useSelector( state => state.name)
    const {src} = useImage({
      srcList: `${Img}`,
    })
    var returnImage = function() {
        if (!Img) {
            return <div>No hay imagen</div>
        }else {
            return <img src={src} />
        }
    }
   
    return returnImage
  }


export default function Random() {  
    const dispatch = useDispatch()
    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(getRandom())
    }
    const Name = useSelector( state => state.name)
    const Id = useSelector( state => state.bday)
    const Alias = useSelector (state => state.alias)
    const Status = useSelector (state => state.status)
    const Occupation = useSelector(state => state.ocupation)
    return (
        <div className='app'>
            <div className='container'>
                <Button style={{width:500, left:480}}onClick={handleSubmit} variant="contained" color="primary">
                    Haga click aqui para obtener un personaje aleatorio
                </Button>
                <div className='card'>
                    <div className='city_max_temp'></div>
                    <div className='city_name'>Nombre: {Name}</div>
                    {console.log(Name)}
                    <div className='city_weather'>Cumplea√±os: {Id}</div>
                    <div className='city_temp'>Ocupacion: {Occupation}</div>
                    <div className='city_min_temp'>Estado: {Status}</div>
                    <div className='city_feels'>Alias: {Alias}</div>
                    <Suspense  fallback={<div>Loading... </div>}>

                    <MyImageComponent  />
                    </Suspense>
                    <div className='character_img'><img ></img></div>

            </div>
            </div>
        </div>
    )
}
