import { GET_RANDOM,  } from './constants'
const initialState = {
    name: '',
    bday: '',
    ocupation:'',
    status: '',
    alias: ''


}

function rootReducer(state = initialState, action) {
    switch (action.type) {
        case GET_RANDOM: {
            return {
                ...state,
                name: action.payload[0].name,
                bday: action.payload[0].char_id,
                alias: action.payload[0].nickname
            }
        }


        default:
            return state
    }
}
export default rootReducer
