import React from 'react'
import './Random.scss'
import {getRandom} from '../redux/actions'
import { useDispatch, useSelector } from 'react-redux';


export default function Random() {  
    const dispatch = useDispatch()
    const handleSubmit = (e) => {
        dispatch(getRandom())
    }
    const Name = useSelector( state => state.name)
    return (
        <div className='app'>
            <div className='container'>
                <div className='card'>
                    <div className='city_max_temp'></div>
                    <div className='city_name'>Nombre: {Name}</div>
                    {console.log(Name)}
                    <div className='city_weather'>Cumpleaños:</div>
                    <div className='city_temp'>Ocupacion</div>
                    <div className='city_min_temp'>Estado</div>
                    <div className='city_feels'>Alias</div>
                <button onClick={handleSubmit}>
                    Haga click aquí para obtener un personaje aleatorio 
                </button>
            </div>
            </div>
        </div>
    )
}
